changeScene=n=>{if(currentScene=GAME_DATA.allScenes.find(e=>e===n),currentScene){GAME_DATA.currentScene=currentScene;var e=document.querySelector("[in-progress]");switch(e&&document.body.removeChild(e),currentScene){case GAME_SCENE:renderWebComponent("sd-game-scene");break;case LAUNCH_SCENE:renderWebComponent("sd-launch-scene");break;default:LOADING_SCENE;renderWebComponent("sd-loading-scene")}}},renderWebComponent=e=>{el=document.createElement(e),el.setAttribute("in-progress",!0),el.style.opacity="0",el.style.display="block",el.style.transition="opacity 0.3s",document.body.appendChild(el),setTimeout(()=>{el.style.opacity="1"},200)},changeScene(LOADING_SCENE),window.addEventListener("scene-change",e=>{changeScene(e.detail)});const components=["sd-button","sd-game-scene","sd-launch-scene"].map(e=>customElements.whenDefined(e));Promise.all(components).then(()=>{console.log(`${components.length} async web components defined`),setTimeout(()=>{changeScene(LAUNCH_SCENE)},1e3)}),window.addEventListener("show-game",e=>changeScene(GAME_SCENE)),window.addEventListener("show-controls",e=>alert("coming soon"));